mapper.update:
		UPDATE EMP
		SET
    		emp_name = #{emp_name}
    		,emp_status = #{emp_status}
    		,dept_no = #{dept_no}
    		,rank_no = #{rank_no}
    		,emp_job = #{emp_job}
    		,emp_retiredate = #{emp_retiredate}
    		,emp_email = #{emp_email}
    		,emp_tel = #{emp_tel}
    		,emp_auth = #{emp_auth}
    		,emp_picture_path = #{emp_picture_path}
		WHERE emp_no =#{emp_no}
