mapper.select:
		    SELECT attendance_no, emp_no, attendance_start_work
                FROM attendance
              WHERE emp_no = #{emp_no}
                   AND SUBSTR(TO_CHAR(attendance_start_work),1,5)
                   = #{yy/mm}

		SELECT schedule_code, emp_no, schedule_type, schedule_title, schedule_startdate, schedule_enddate, schedule_content
 		  FROM schedule
		 WHERE schedule_type = #{schedule_type}
